'use strict';

// MODULES //

var tape = require( 'tape' );
var UINT32_MAX = require( '@stdlib/constants/math/uint32-max' );
var FLOAT64_MAX_SAFE_INTEGER = require('@stdlib/constants/math/float64-max-safe-integer');
var isPositiveInteger = require( '@stdlib/math/base/assert/is-positive-integer' );
var mt19937 = require( './../lib' );


// VARIABLES //

var NORMALIZED_NORMALIZATION_CONSTANT = 1.0 / (FLOAT64_MAX_SAFE_INTEGER + 1.0);


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof mt19937, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to generate normalized pseudorandom numbers', function test( t ) {
	t.equal( typeof mt19937.normalized, 'function', 'has `normalized` method' );
	t.end();
});

tape( 'attached to the main export is a method to generate mersenne twister pseudorandom number generator', function test( t ) {
	t.equal( typeof mt19937.factory, 'function', 'has `factory` method' );
	t.end();
});

tape( 'attached to the main export is the generator name', function test( t ) {
	t.equal( mt19937.NAME, 'mt19937', 'has `NAME` property' );
	t.end();
});

tape( 'attached to the main export is the generator seed', function test( t ) {
	t.equal( typeof mt19937.SEED, 'number', 'has `SEED` property' );
	t.end();
});

tape( 'attached to the main export is the minimum possible generated number', function test( t ) {
	t.equal( mt19937.MIN, 1, 'has `MIN` property' );
	t.end();
});

tape( 'attached to the main export is the maximum possible generated number', function test( t ) {
	t.equal( mt19937.MAX, UINT32_MAX, 'has `MAX` property' );
	t.end();
});

tape( 'the function returns pseudorandom integers strictly between 0 and 2^32-1 (inclusive)', function test( t ) {
	var v;
	var i;
	for ( i = 0; i < 1e4; i++ ) {
		v = mt19937();
		t.equal( typeof v, 'number', 'returns a number' );
		t.equal( isPositiveInteger( v ), true, 'returns a positive integer' );
		t.equal( v >= 1 && v <= UINT32_MAX, true, 'returns an integer between 1 and 2^32-1 (inclusive)' );
	}
	t.end();
});

tape( 'the `normalized` method returns pseudorandom numbers strictly between 0 (inclusive) and 1 (exclusive)', function test( t ) {
	var v;
	var i;
	for ( i = 0; i < 1e4; i++ ) {
		v = mt19937.normalized();
		t.equal( typeof v, 'number', 'returns a number' );
		t.equal( v >= 0.0 && v < 1.0, true, 'returns a number between 0 (inclusive) and 1 (exclusive)' );
	}
	t.end();
});

tape( 'attached to the `normalized` method is the generator name', function test( t ) {
	t.equal( mt19937.normalized.NAME, 'mt19937', 'has `NAME` property' );
	t.end();
});

tape( 'attached to the `normalized` method is the generator seed', function test( t ) {
	t.equal( typeof mt19937.normalized.SEED, 'number', 'has `SEED` property' );
	t.end();
});

tape( 'attached to the `normalized` method is the minimum possible generated number', function test( t ) {
	t.equal( mt19937.normalized.MIN, 0.0, 'has `MIN` property' );
	t.end();
});

tape( 'attached to the `normalized` method is the maximum possible generated number', function test( t ) {
	var x = UINT32_MAX >>> 5;
	var y = UINT32_MAX >>> 6;
	t.equal( mt19937.normalized.MAX, ( ( x * 67108864.0 ) + y ) * NORMALIZED_NORMALIZATION_CONSTANT, 'has `MAX` property' );
	t.end();
});

tape( 'the function returns pseudorandom numbers from the Mersenne Twister PRNG (integer seed)', function test( t ) {
	var expected;
	var rand;
	var i;

	/* Tested against the original C source
	The main function to generate these values
	int main(void) {
		int i;
		init_genrand(1234);
		for (int i = 0; i < 10; ++i) {
			printf("%10lu ", genrand_int32());
		}
		return 0;
	} */
	expected = [
		822569775,
		2137449171,
		2671936806,
		3512589365,
		1880026316,
		2629000564,
		3373089432,
		3312965625,
		3349970575,
		3696548529
	];
	rand = mt19937.factory( 1234 );
	for ( i = 0; i < expected.length; i++ ) {
		t.equal( rand(), expected[ i ], 'the ' + i + 'th value matches' );
	}
	t.end();
});

tape( 'the function returns pseudorandom numbers from the Mersenne Twister PRNG (array seed)', function test( t ) {
	var expected;
	var rand;
	var i;

	/* Tested against the original C source
	The main function to generate these values
	int main(void) {
		int i;
		unsigned long init[4]={123, 234, 345, 456}, length=4;
		init_by_array(init, length);
		for (i = 0; i < 10; ++i) {
		printf("%10lu \n", genrand_int32());
		}
		return 0;
	} */
	expected = [
		214471079,
		4279110828,
		3304418324,
		396089618,
		3598660246,
		1454062796,
		378435647,
		358238245,
		3904315353,
		2530152577
	];
	rand = mt19937.factory( [ 123, 234, 345, 456 ] );
	for ( i = 0; i < expected.length; i++ ) {
		t.equal( rand(), expected[ i ], 'the ' + i + 'th value matches' );
	}
	t.end();
});

tape( 'the attached normalized function returns normalized pseudorandom numbers using the Mersenne Twister PRNG (array seed)', function test( t ) {
	var expected;
	var rand;
	var i;

	/* Tested against the original C source
	The main function to generate these values
	int main(void) {
		int i;
		unsigned long init[4]={123, 234, 345, 456}, length=4;
		init_by_array(init, length);
		for (i = 0; i < 10; ++i) {
		printf("%.15f \n", genrand_res53());
		}
		return 0;
	} */
	expected = [
		0.049935445161969,
		0.769369841309054,
		0.837878378767896,
		0.088111408676273,
		0.909044255235024,
		0.020387033688463,
		0.866302487125788,
		0.664679979415962,
		0.883013637028643,
		0.058767231447210
	];
	rand = mt19937.factory( [ 123, 234, 345, 456 ] ).normalized;
	for ( i = 0; i < expected.length; i++ ) {
		t.equal( parseFloat( rand().toFixed( 15 ) ), expected[ i ], 'the ' + i + 'th value matches' );
	}
	t.end();
});
