'use strict';

// MODULES //

var tape = require( 'tape' );
var ENV = require( '@stdlib/process/env' );
var kstest = require( '@stdlib/stats/kstest' );
var round = require( '@stdlib/math/base/special/round' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var factory = require( './../lib/factory.js' );


// VARIABLES //

var opts = {
	'skip': ( ENV.TEST_MODE === 'coverage' )
};


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof factory, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns a pseudorandom number generator (no seed)', function test( t ) {
	var pareto1;
	var r;
	var i;

	// When binding distribution parameters...
	pareto1 = factory( 2.0, 2.0 );
	for ( i = 0; i < 100; i++ ) {
		r = pareto1();
		t.equal( typeof r, 'number', 'returns a number' );
	}

	// Without binding distribution parameters...
	pareto1 = factory();
	for ( i = 0; i < 100; i++ ) {
		r = pareto1( 2.5, 2.0 );
		t.equal( typeof r, 'number', 'returns a number' );
	}

	t.end();
});

tape( 'the function returns a seeded pseudorandom number generator', function test( t ) {
	var pareto1a;
	var pareto1b;
	var seed;
	var r1;
	var r2;
	var i;

	seed = round( Date.now() / 1000 );

	pareto1a = factory( 1.0, 1.0, {
		'seed': seed
	});
	pareto1b = factory( 1.0, 1.0, {
		'seed': seed
	});

	t.notEqual( pareto1a, pareto1b, 'separate generators' );

	for ( i = 0; i < 100; i++ ) {
		r1 = pareto1a();
		r2 = pareto1b();
		t.equal( r1, r2, 'both return same number' );
	}
	t.end();
});

tape( 'attached to the returned function is the generator name', function test( t ) {
	var pareto1 = factory();
	t.equal( pareto1.NAME, 'pareto-type1', 'has `NAME` property' );
	t.end();
});

tape( 'attached to the returned function is the underlying PRNG', function test( t ) {
	var pareto1 = factory();
	t.equal( typeof pareto1.PRNG, 'function', 'has `PRNG` property' );
	t.end();
});

tape( 'attached to the returned function is the generator seed', function test( t ) {
	var pareto1 = factory({
		'seed': 12345
	});
	t.equal( typeof pareto1.SEED, 'number', 'has `SEED` property' );
	t.equal( pareto1.SEED, 12345, 'equal to provided seed' );
	t.end();
});

tape( 'the function throws an error if provided a shape argument which is not a positive number', function test( t ) {
	var values;
	var i;

	values = [
		-2.0,
		0.0,
		'5',
		null,
		true,
		undefined,
		NaN,
		[],
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			factory( value, 2.0 );
		};
	}
});

