'use strict';

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var isObject = require( '@stdlib/assert/is-plain-object' );
var randu = require( '@stdlib/random/base/randu' ).factory;
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var validate = require( './validate.js' );
var weibull0 = require( './_weibull.js' );


// MAIN //

/**
* Returns a pseudorandom number generator for generating Weibull distributed random numbers.
*
* @param {PositiveNumber} [k] - scale parameter
* @param {PositiveNumber} [lambda] - shape parameter
* @param {Options} [options] - function options
* @param {*} [options.seed] - pseudorandom number generator seed
* @throws {TypeError} `k` must be a positive number
* @throws {TypeError} `lambda` must be a positive number
* @throws {TypeError} options argument must be an object
* @returns {Function} pseudorandom number generator
*
* @example
* var weibull = factory( 2.0, 1.0 );
* var v = weibull();
* // returns <number>
*
* @example
* var weibull = factory( 2.0, 2.0, {
*     'seed': 297
* });
* var v = weibull();
* // returns ~1.288
*/
function factory() {
	var lambda;
	var opts;
	var rand;
	var prng;
	var err;
	var k;

	if ( arguments.length === 0 ) {
		rand = randu();
	} else if ( arguments.length === 1 ) {
		opts = arguments[ 0 ];
		if ( !isObject( opts ) ) {
			throw new TypeError( 'invalid input argument. Options argument must be an object. Value: `' + opts + '`.' );
		}
		rand = randu( opts );
	} else {
		lambda = arguments[ 0 ];
		k = arguments[ 1 ];
		err = validate( lambda, k );
		if ( err ) {
			throw err;
		}
		if ( arguments.length > 2 ) {
			opts = arguments[ 2 ];
			if ( !isObject( opts ) ) {
				throw new TypeError( 'invalid input argument. Options argument must be an object. Value: `' + opts + '`.' );
			}
			rand = randu( opts );
		} else {
			rand = randu();
		}
	}
	if ( lambda === void 0 ) {
		prng = weibull2;
	} else {
		prng = weibull1;
	}
	setReadOnly( prng, 'NAME', 'weibull' );
	setReadOnly( prng, 'SEED', rand.SEED );
	setReadOnly( prng, 'PRNG', rand );

	return prng;

	/**
	* Returns a pseudorandom number drawn from a Weibull distribution.
	*
	* @private
	* @returns {NonNegativeNumber} pseudorandom number
	*
	* @example
	* var v = weibull1();
	* // returns <number>
	*/
	function weibull1() {
		return weibull0( rand, k, lambda );
	}

	/**
	* Returns a pseudorandom number drawn from a Weibull distribution.
	*
	* @private
	* @param {PositiveNumber} k - scale parameter
	* @param {PositiveNumber} lambda - shape parameter
	* @returns {NonNegativeNumber} pseudorandom number
	*
	* @example
	* var v = weibull2( 2.0, 1.0 );
	* // returns <number>
	*
	* @example
	* var v = weibull2( 3.0, 0.0 );
	* // returns NaN
	*
	* @example
	* var v = weibull2( 0.0, 2.0 );
	* // returns NaN
	*
	* @example
	* var v = weibull2( NaN, NaN );
	* // returns NaN
	*/
	function weibull2( k, lambda ) {
		if (
			isnan( k ) ||
			isnan( lambda ) ||
			k <= 0.0 ||
			lambda <= 0.0
		) {
			return NaN;
		}
		return weibull0( rand, k, lambda );
	}
}


// EXPORTS //

module.exports = factory;
