'use strict';

/*
* When adding modules to the namespace, ensure that they are added in alphabetical order according to module name.
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );


// MAIN //

/**
* Top-level namespace.
*
* @namespace ns
*/
var ns = {};

/**
* @name arcsine
* @memberof ns
* @readonly
* @type {Function}
* @see {@link module:@stdlib/random/iterators/arcsine}
*/
setReadOnly( ns, 'arcsine', require( '@stdlib/random/iterators/arcsine' ) );

/**
* @name bernoulli
* @memberof ns
* @readonly
* @type {Function}
* @see {@link module:@stdlib/random/iterators/bernoulli}
*/
setReadOnly( ns, 'bernoulli', require( '@stdlib/random/iterators/bernoulli' ) );

/**
* @name beta
* @memberof ns
* @readonly
* @type {Function}
* @see {@link module:@stdlib/random/iterators/beta}
*/
setReadOnly( ns, 'beta', require( '@stdlib/random/iterators/beta' ) );

/**
* @name betaprime
* @memberof ns
* @readonly
* @type {Function}
* @see {@link module:@stdlib/random/iterators/betaprime}
*/
setReadOnly( ns, 'betaprime', require( '@stdlib/random/iterators/betaprime' ) );

/**
* @name uniform
* @memberof ns
* @readonly
* @type {Function}
* @see {@link module:@stdlib/random/iterators/uniform}
*/
setReadOnly( ns, 'uniform', require( '@stdlib/random/iterators/uniform' ) );


// EXPORTS //

module.exports = ns;
