'use strict';

// MODULES //

var tape = require( 'tape' );
var RE_DURATION = require( './../lib' );


// TESTS //

tape( 'main export is a regular expression', function test( t ) {
	t.ok( true, __filename );
	t.equal( RE_DURATION instanceof RegExp, true, 'main export is a regular expression' );
	t.end();
});

tape( 'the regular expression captures a duration string', function test( t ) {
	var values;
	var i;

	values = [
		'3d',
		'3D',
		'3h',
		'3H',
		'3m',
		'3M',
		'3s',
		'3S',
		'3ms',
		'3MS',
		'3us',
		'3US',
		'3ns',
		'3NS',
		'3s4ms',
		'3S4MS',
		'3s4ms5us',
		'3S4MS5US',
		'3s4ms5us6ns',
		'3S4MS5US6NS',
		'3h4m5s',
		'3H4M5S',
		'3h4m5s6ms',
		'3H4M5S6MS',
		'3h4m5s6ms7us',
		'3H4M5S6MS7US',
		'3h4m5s6ms7us8ns',
		'3H4M5S6MS7US8NS',
		'3d4h5m6s',
		'3D4H5M6S',
		'3d4h5m6s7ms',
		'3D4H5M6S7MS',
		'3d4h5m6s7ms8us',
		'3D4H5M6S7MS8US',
		'3d4h5m6s7ms8us9ns',
		'3D4H5M6S7MS8US9NS',
		'3d4h5m6s7ms8us9ns10s',
		'3D4H5M6S7MS8US9NS10S',
		'3d4h5m6s7ms8us9ns10s11ms',
		'3D4H5M6S7MS8US9NS10S11MS',
		'3d4h5m6s7ms8us9ns10s11ms12us',
		'3D4H5M6S7MS8US9NS10S11MS12US',
		'3d4h5m6s7ms8us9ns10s11ms12us13ns',
		'3D4H5M6S7MS8US9NS10S11MS12US13NS'
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( RE_DURATION.test( values[i] ), true, values[i] );
	}
	t.end();
});


