'use strict';

// MODULES //

var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var RE = require( './../lib' );


// TESTS //

tape( 'main export is a regular expression', function test( t ) {
	t.ok( true, __filename );
	t.equal( RE instanceof RegExp, true, 'main export is a regular expression' );
	t.end();
});

tape( 'attached to main export is a regular expression for POSIX filenames', function test( t ) {
	t.equal( RE.posix instanceof RegExp, true, '`posix` export is a regular expression' );
	t.end();
});

tape( 'attached to main export is a regular expression for Windows filenames', function test( t ) {
	t.equal( RE.win32 instanceof RegExp, true, '`win32` export is a regular expression' );
	t.end();
});

tape( 'the main export is a POSIX specific regular expression if on a POSIX platform', function test( t ) {
	var RE;

	RE = proxyquire( './../lib', {
		'@stdlib/assert/is-windows': false
	});

	t.equal( RE.toString(), RE.posix.toString(), 'main export is `posix` regexp: '+RE.toString() );
	t.end();
});

tape( 'the main export is a Windows specific regular expression if on a Windows platform', function test( t ) {
	var RE;

	RE = proxyquire( './../lib', {
		'@stdlib/assert/is-windows': true
	});

	t.equal( RE.toString(), RE.win32.toString(), 'main export is `win32` regexp: '+RE.toString() );
	t.end();
});

tape( 'the `posix` regular expression captures POSIX filename extensions', function test( t ) {
	var expected;
	var values;
	var ext;
	var i;

	values = [
		'index.js',
		'/foo/bar/home.html'
	];

	expected = [
		'.js',
		'.html'
	];

	for ( i = 0; i < values.length; i++ ) {
		ext = RE.posix.exec( values[ i ] )[ 1 ];
		t.equal( ext, expected[ i ], values[ i ] );
	}
	t.end();
});

tape( 'the `win32` regular expression captures Windows filename extensions', function test( t ) {
	var expected;
	var values;
	var ext;
	var i;

	values = [
		'index.js',
		'C:\\foo\\bar\\home.html'
	];

	expected = [
		'.js',
		'.html'
	];

	for ( i = 0; i < values.length; i++ ) {
		ext = RE.win32.exec( values[ i ] )[ 1 ];
		t.equal( ext, expected[ i ], values[ i ] );
	}
	t.end();
});
