    > function onDuration( value, next ) {
    ...     setTimeout( onTimeout, value );
    ...     function onTimeout() {
    ...         console.log( value );
    ...         next();
    ...     }
    ... };
    > function done( error ) {
    ...     if ( error ) {
    ...         throw error;
    ...     }
    ...     console.log( 'Done.' );
    ... };
    > var arr = [ 1000, 2500, 3000 ];
    > forEachRightAsync( arr, onDuration, done )
    1000
    2500
    3000
    Done.

    // Limit number of concurrent invocations:
    > function onDuration( value, next ) {
    ...     setTimeout( onTimeout, value );
    ...     function onTimeout() {
    ...         console.log( value );
    ...         next();
    ...     }
    ... };
    > function done( error ) {
    ...     if ( error ) {
    ...         throw error;
    ...     }
    ...     console.log( 'Done.' );
    ... };
