    ... };
    > function done( error, out ) {
    ...     if ( error ) {
    ...         throw error;
    ...     }
    ...     console.log( out );
    ... };
    > var opts = { 'limit': 2 };
    > var obj = { 'a': 1, 'b': 2, 'c': 3 };
    > mapValuesAsync( obj, opts, transform, done )
