'use strict';

// MODULES //

var startsWith = require( '@stdlib/string/starts-with' );
var fuzzyMatch = require( './fuzzy_match.js' );


// MAIN //

/**
* Filters a list of strings based on a provided search string.
*
* @private
* @param {Array} out - output array
* @param {Array} arr - source array
* @param {string} str - string filter
* @param {boolean} isFuzzy - boolean indicating if the completions should be fuzzy
* @returns {Array} output array
*/
function filterByPrefix( out, arr, str, isFuzzy ) {
	var fuzzyResults = [];
	var match;
	var i;

	if ( isFuzzy ) {
		for ( i = 0; i < arr.length; i++ ) {
			match = fuzzyMatch( arr[ i ], str );
			if ( match ) {
				fuzzyResults.push( match );
			}
		}
		for ( i = 0; i < fuzzyResults.length; i++ ) {
			out.push( fuzzyResults[ i ] );
		}
	} else {
		for ( i = 0; i < arr.length; i++ ) {
			if ( startsWith( arr[ i ], str ) ) {
				out.push( arr[ i ] );
			}
		}
	}
	return out;
}


// EXPORTS //

module.exports = filterByPrefix;
