'use strict';

// MODULES //

var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var ns = require( './../lib/functions/namespace.js' );


// FIXTURES //

function namespace() {
	var ns = [
		{
			'alias': 'beep',
			'value': {}
		},
		{
			'alias': 'boop',
			'value': []
		},
		{
			'alias': 'bop',
			'value': {}
		}
	];
	ns[ '@noCallThru' ] = true;
	return ns;
}


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof ns, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function logs namespace aliases', function test( t ) {
	var expected;
	var actual;
	var mock;
	var ns;

	mock = {
		'log': log
	};

	expected = [
		'beep',
		'boop',
		'bop'
	];
	actual = [];

	ns = proxyquire( './../lib/functions/namespace.js', {
		'./../namespace.js': namespace(),
		'./../console.js': mock
	});

	ns();

	t.deepEqual( actual, expected, 'logs expected values' );
	t.end();

	function log( str ) {
		actual.push( str );
	}
});
