'use strict';

var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var pmf = require( './../lib' );

var i;
var N;
var lambda;
var x;
var y;

for ( i = 0; i < 10; i++ ) {
	lambda = randu();
	N = round( randu() * 20.0 );
	x = round( randu() * N );
	y = pmf( x, lambda, N );
	console.log( 'x: %d, λ: %d, N: %d, P(X=x;λ,N): %d', x, lambda.toFixed( 4 ), N, y.toFixed( 4 ) );
}
