'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var abs = require( '@stdlib/math/base/special/abs' );
var PINF = require( '@stdlib/constants/float64/pinf' );
var EPS = require( '@stdlib/constants/float64/eps' );
var pmf = require( './../lib' );


// FIXTURES //

var data = require( './fixtures/julia/data.json' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof pmf, 'function', 'main export is a function' );
	t.end();
});

tape( 'if provided `NaN` for any parameter, the function returns `NaN`', function test( t ) {
	var y = pmf( NaN, 1.5, 10 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = pmf( 3, NaN, 10 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = pmf( 3, 1.5, NaN );
	t.equal( isnan( y ), true, 'returns NaN' );

	t.end();
});

tape( 'if provided an integer `x` greater than `N`, the function returns `0` (provided all parameters are valid)', function test( t ) {
	var y = pmf( 12, 1.2, 10 );
	t.equal( y, 0.0, 'returns 0' );

	y = pmf( 100, 1.2, 10 );
	t.equal( y, 0.0, 'returns 0' );

	t.end();
});

tape( 'if provided an `N` which is not a nonnegative integer, the function returns `NaN`', function test( t ) {
	var y;

	y = pmf( 2, 3.5, -10 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = pmf( 2, 2.5, 10.5 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = pmf( 2, 2.5, PINF );
	t.equal( isnan( y ), true, 'returns NaN' );

	t.end();
});

tape( 'if provided a `λ` which is not a nonnegative integer, the function returns `NaN`', function test( t ) {
	var y;

	y = pmf( 2, -3.5, 10 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = pmf( 2, -2, 10 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = pmf( 0, PINF, 10 );
	t.equal( isnan( y ), true, 'returns NaN' );

	t.end();
});

tape( 'the function evaluates the pmf for `x`', function test( t ) {
	var expected;
	var lambda;
	var delta;
	var tol;
	var x;
	var N;
	var y;
	var i;

	expected = data.expected;
	x = data.x;
	lambda = data.lambda;
	N = data.N;
	for ( i = 0; i < x.length; i++ ) {
		y = pmf( x[i], lambda[i], N[i] );
		if ( y === expected[i] ) {
			t.equal( y, expected[i], 'x: '+x[i]+', lambda: '+lambda[i]+', N: '+N[i]+', y: '+y+', expected: '+expected[i] );
		} else {
			delta = abs( y - expected[ i ] );
			tol = 1040.0 * EPS * abs( expected[ i ] );
			t.ok( delta <= tol, 'within tolerance. x: '+x[i]+'. lambda: '+lambda[i]+'. N: '+N[i]+'. y: '+y+'. E: '+expected[ i ]+'. Δ: '+delta+'. tol: '+tol+'.' );
		}
	}
	t.end();
});
