'use strict';

var objectKeys = require( '@stdlib/utils/keys' );
var geometric = require( './../lib' );

console.log( objectKeys( geometric ) );

var p = 0.1;
var it = geometric.stdev(p);
console.log(it);
// => ~9.487

p = 0.5;
it = geometric.stdev(p);
console.log(it);
// => ~1.414

it = geometric.stdev(NaN);
console.log(it);
// => NaN

p = 1.5;
it = geometric.stdev(p);
console.log(it);
// => NaN

p = -1.0;
it = geometric.stdev(p);
console.log(it);
// => NaN
