'use strict';

/**
* Rademacher distribution cumulative distribution function (CDF).
*
* @module @stdlib/stats/base/dists/rademacher/cdf
*
* @example
* var cdf = require( '@stdlib/stats/base/dists/rademacher/cdf' );
*
* var v = cdf( 0.0 );
* // returns 0.5
*
* v = cdf( 1.0 );
* // returns 1.0
*
* v = cdf( -2.0 );
* // returns 0.0
*
* v = cdf( NaN );
* // returns NaN
*/

// MODULES //

var cdf = require( './main.js' );


// EXPORTS //

module.exports = cdf;
