'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/assert/is-nan' );


// MAIN //

/**
* Evaluates the cumulative distribution function (CDF) for a Rademacher distribution.
*
* @param {number} k - input value
* @returns {Probability} evaluated CDF
*
* @example
* var v = cdf( 0.0 );
* // returns 0.5
*
* @example
* var v = cdf( 1.0 );
* // returns 1.0
*
* @example
* var v = cdf( -2.0 );
* // returns 0.0
*
* @example
* var v = cdf( NaN );
* // returns NaN
*/
function cdf( k ) {
	if ( isnan( k ) ) {
		return NaN;
	}
	if ( k < -1.0 ) {
		return 0.0;
	}
	if ( k >= 1.0 ) {
		return 1.0;
	}
	return 0.5;
}


// EXPORTS //

module.exports = cdf;
