'use strict';

/*
* When adding modules to the namespace, ensure that they are added in alphabetcial order according to module name.
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );


// MAIN //

/**
 * Top-level namespace.
 *
 * @namespace rademacher
 */
var rademacher = {};

/**
 * @name cdf
 * @memberof rademacher
 * @readonly
 * @type {Function}
 * @see {@link module:@stdlib/stats/base/dists/rademacher/cdf}
 */
setReadOnly( rademacher, 'cdf', require( '@stdlib/stats/base/dists/rademacher/cdf' ) );

/**
 * @name mean
 * @memberof rademacher
 * @readonly
 * @type {Function}
 * @see {@link module:@stdlib/stats/base/dists/rademacher/mean}
 */
setReadOnly( rademacher, 'mean', require( '@stdlib/stats/base/dists/rademacher/mean' ) );

/**
 * @name median
 * @memberof rademacher
 * @readonly
 * @type {Function}
 * @see {@link module:@stdlib/stats/base/dists/rademacher/median}
 */
setReadOnly( rademacher, 'median', require( '@stdlib/stats/base/dists/rademacher/median' ) );

/**
 * @name pmf
 * @memberof rademacher
 * @readonly
 * @type {Function}
 * @see {@link module:@stdlib/stats/base/dists/rademacher/pmf}
 */
setReadOnly( rademacher, 'pmf', require( '@stdlib/stats/base/dists/rademacher/pmf' ) );

module.exports = rademacher;
