// MAIN //

/**
* Returns the expected value of a Rademacher distribution.
*
* @returns {number} expected value
*
* @example
* var v = mean();
* // returns 0.0
*/
function mean() {
	return 0.0;
}


// EXPORTS //

module.exports = mean;
