'use strict';

/**
* Rademacher distribution probability mass function (PMF).
*
* @module @stdlib/stats/base/dists/rademacher/pmf
*
* @example
* var pmf = require( '@stdlib/stats/base/dists/rademacher/pmf' );
*
* var y = pmf( -1.0 );
* // returns 0.5
*
* y = pmf( 1.0 );
* // returns 0.5
*
* y = pmf( 0.4 );
* // returns 0.0
*
* y = pmf( NaN );
* // returns NaN
*
* y = pmf( 1.2 );
* // returns NaN
*
* y = pmf( -2.4 );
* // returns NaN
*/

// MODULES //

var pmf = require( './main.js' );


// EXPORTS //

module.exports = pmf;
