'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/assert/is-nan' );

/**
* Evaluates the probability mass function (PMF) for a Rademacher distribution.
*
* @param {number} x - input value
* @returns {Probability} evaluated PMF
*
* @example
* var y = pmf( -1.0 );
* // returns 0.5
*
* @example
* y = pmf( 1.0 );
* // returns 0.5
*
* @example
* y = pmf( 0.4 );
* // returns 0.0
*
* @example
* y = pmf( NaN );
* // returns NaN
*
* @example
* y = pmf( 1.2 );
* // returns NaN
*
* @example
* y = pmf( -2.4 );
* // returns NaN
*/
function pmf( x ) {
	if (
		isnan( x ) ||
		x < -1.0 ||
		x > 1.0
	) {
		return NaN;
	}
	if ( x === 1.0 || x === -1.0 ) {
		return 0.5;
	}
	return 0.0;
}


// EXPORTS //

module.exports = pmf;
