'use strict';

var poisson = require( '@stdlib/random/base/poisson' );
var chi2gof = require( './../lib' );

var lambda;
var table;
var rpois;
var freqs;
var len;
var out;
var val;
var i;
var x;

// Draw 400 samples from a Poisson( 3.0 ) distribution:
lambda = 3.0;
rpois = poisson.factory( lambda );

len = 400;
x = new Array( len );
for ( i = 0; i < len; i++ ) {
	x[ i ] = rpois();
}

// Generate frequency table:
freqs = [];
for ( i = 0; i < len; i++ ) {
	val = x[ i ];
	if ( freqs[ val ] === void 0 ) {
		freqs[ val ] = 1;
	} else {
		freqs[ val ] += 1;
	}
}

// Fill holes in array:
for ( i = 0; i < freqs.length; i++ ) {
	if ( freqs[ i ] === void 0 ) {
		freqs[ i ] = 0;
	}
}

out = chi2gof( freqs, 'poisson', lambda );
table = out.print();

console.log( out );
console.log( '\n' );
console.log( table );
