'use strict';


// MODULES //

var abs = require( '@stdlib/math/base/special/abs' );
var isnan = require( '@stdlib/assert/is-nan' );
var fourthPi = require( '@stdlib/constants/math/float64-fourth-pi' );
var halfPi = require( '@stdlib/constants/math/float64-half-pi' );
var cos = require( '@stdlib/math/base/special/cos' );


// MAIN //

/**
* Computes the Cosine kernel for a value.
*
* @private
* @param {number} u - value for which we wish to calculate the Cosine kernel
* @returns {number} the value for the kernel at u
*
* @example
* var u = 5;
* out = cosine(u); // returns 0
*/
function cosine(u) {
	var absU;

	if (isnan(u)) {
		return NaN;
	}

	absU = abs(u);
	if (absU > 1) {
		return 0;
	}

	return fourthPi * cos(halfPi * u);
}


// EXPORTS //

module.exports = cosine;
