'use strict';


// MODULES //

var abs = require( '@stdlib/math/base/special/abs' );
var isnan = require( '@stdlib/assert/is-nan' );
var pow = require( '@stdlib/math/base/special/pow' );


// MAIN //

/**
* Computes the Quartic kernel for a value.
*
* @private
* @param {number} u - value for which we wish to calculate the Quartic kernel
* @returns {number} the value for the kernel at u
*
* @example
* var u = 5;
* out = quartic(u); // returns 0
*/
function quartic(u) {
	var absU;

	if (isnan(u)) {
		return NaN;
	}

	absU = abs(u);
	if (absU > 1) {
		return 0;
	}
	return (15 / 16) * pow(1 - pow(u, 2), 2);
}


// EXPORTS //

module.exports = quartic;
