'use strict';


// MODULES //

var abs = require( '@stdlib/math/base/special/abs' );
var isnan = require( '@stdlib/assert/is-nan' );
var pow = require( '@stdlib/math/base/special/pow' );


// MAIN //

/**
* Computes the Tricube kernel for a value.
*
* @private
* @param {number} u - value for which we wish to calculate the Tricube kernel
* @returns {number} the value for the kernel at u
*
* @example
* var u = 5;
* out = tricube(u); // returns 0
*/
function tricube(u) {
	var absU;

	if (isnan(u)) {
		return NaN;
	}

	absU = abs(u);
	if (absU > 1) {
		return 0;
	}
	return (70 / 81) * pow(1 - pow(absU, 3), 3);
}


// EXPORTS //

module.exports = tricube;
