'use strict';


// MODULES //

var abs = require( '@stdlib/math/base/special/abs' );
var isnan = require( '@stdlib/assert/is-nan' );


// MAIN //

/**
* Computes the uniform kernel for a value.
*
* @private
* @param {number} u - value for which we wish to calculate the triangular kernel
* @returns {number} the value for the kernel at u
*
* @example
* var u = 5;
* out = uniform(u); // returns 0
*/
function uniform(u) {
	var absU;

	if (isnan(u)) {
		return NaN;
	}

	absU = abs(u);
	if (absU > 1) {
		return 0;
	}
	return 0.5;
}


// EXPORTS //

module.exports = uniform;
