'use strict';

/**
* Computes the maximum value of a numeric array.
*
* @private
* @param {Array} arr - array of values
* @returns {number} max value
*
* @example
* var arr = [ 2, 5, 3, -10 ];
* var y = max( arr );
* // returns 5
*/
function max( arr ) {
	var len;
	var val;
	var i;

	val = arr[ 0 ];
	len = arr.length;
	for ( i = 1; i < len; i++ ) {
		if ( arr[ i ] > val ) {
			val = arr[ i ];
		}
	}
	return val;
}


// EXPORTS //

module.exports = max;
