'use strict';

/**
* Returns the arithmetic range of an array.
*
* @private
* @param {Array} arr - input array
* @returns {Array} arithmetic range
*/
function range( arr ) {
	var len;
	var max;
	var min;
	var i;
	var x;

	len = arr.length;
	min = arr[ 0 ];
	max = min;
	for ( i = 1; i < len; i++ ) {
		x = arr[ i ];
		if ( x < min ) {
			min = x;
		} else if ( x > max ) {
			max = x;
		}
	}
	return [ min, max ];
}


// EXPORTS //

module.exports = range;
