'use strict';

var stdin = require( '@stdlib/streams/base/stdin' );
var stderr = require( './../lib' );

// Set the encoding:
stdin.setEncoding( 'utf8' );

// Create an echo stream:
stdin.pipe( stderr );

// Push data to `stdin`:
stdin.push( 'beep' );
stdin.push( ' ' );
stdin.push( 'boop' );
stdin.push( '\n' );

// End the stream:
stdin.push( null );

// Ensure the process closes:
setTimeout( process.exit, 1000 ); // eslint-disable-line no-process-exit
