'use strict';

/**
* Transform stream which splits streamed data.
*
* @module @stdlib/streams/utils/split
*
* @example
* var stdout = require( '@stdlib/streams/base/stdout' );
* var splitStream = require( '@stdlib/streams/utils/split' );
*
* var stream = splitStream();
*
* stream.pipe( stdout );
*
* stream.write( '1\n2\n3' );
* stream.end();
* // => '1' => '2' => '3'
*
* @example
* var splitStream = require( '@stdlib/streams/utils/split' );
*
* var opts = {
*     'sep': '\t',
*     'objectMode': true,
*     'encoding': 'utf8',
*     'highWaterMark': 64
* };
*
* var factory = splitStream.factory( opts );
*
* // Create 10 identically configured streams...
* var streams = [];
* var i;
* for ( i = 0; i < 10; i++ ) {
*     streams.push( factory() );
* }
*
* @example
* var stdout = require( '@stdlib/streams/base/stdout' );
* var splitStream = require( '@stdlib/streams/utils/split' );
*
* var stream = splitStream.objectMode({
*     'sep': ','
* });
*
* stream.pipe( stdout );
*
* stream.write( 'a,b,c' );
* stream.end();
* // => 'a' => 'b' => 'c'
*/


// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var stream = require( './stream.js' );
var objectMode = require( './object_mode.js' );
var factory = require( './factory.js' );


// METHODS //

setReadOnly( stream, 'objectMode', objectMode );
setReadOnly( stream, 'factory', factory );


// EXPORTS //

module.exports = stream;
