/**
* Header file for strided array data types.
*/
#ifndef STDLIB_STRIDED_DTYPES_H
#define STDLIB_STRIDED_DTYPES_H

/**
* Enumeration of number of bytes necessary to store a strided array data type.
*/
enum STDLIB_STRIDED_DTYPE_NBYTES {
	STDLIB_STRIDED_BOOL_NBYTES = 0,

	STDLIB_STRIDED_INT8_NBYTES = 1,
	STDLIB_STRIDED_UINT8_NBYTES = 1,
	STDLIB_STRIDED_INT16_NBYTES = 2,
	STDLIB_STRIDED_UINT16_NBYTES = 2,
	STDLIB_STRIDED_INT32_NBYTES = 4,
	STDLIB_STRIDED_UINT32_NBYTES = 4,
	STDLIB_STRIDED_INT64_NBYTES = 8,
	STDLIB_STRIDED_UINT64_NBYTES = 8,
	STDLIB_STRIDED_INT128_NBYTES = 16,
	STDLIB_STRIDED_UINT128_NBYTES = 16,
	STDLIB_STRIDED_INT256_NBYTES = 32,
	STDLIB_STRIDED_UINT256_NBYTES = 32,

	STDLIB_STRIDED_FLOAT16_NBYTES = 2,
	STDLIB_STRIDED_FLOAT32_NBYTES = 4,
	STDLIB_STRIDED_FLOAT64_NBYTES = 8,
	STDLIB_STRIDED_FLOAT128_NBYTES = 16
};

/**
* Enumeration of the single letter character abbreviation for strided array data types.
*/
enum STDLIB_STRIDED_DTYPE_CHAR {
	STDLIB_STRIDED_BOOL_CHAR = '?',

	STDLIB_STRIDED_INT8_CHAR = 'b',
	STDLIB_STRIDED_UINT8_CHAR = 'B',
	STDLIB_STRIDED_INT16_CHAR = 'h',
	STDLIB_STRIDED_UINT16_CHAR = 'H',
	STDLIB_STRIDED_INT32_CHAR = 'i',
	STDLIB_STRIDED_UINT32_CHAR = 'I',
	STDLIB_STRIDED_INT64_CHAR = 'l',
	STDLIB_STRIDED_UINT64_CHAR = 'L',
	STDLIB_STRIDED_INT128_CHAR = 'n',
	STDLIB_STRIDED_UINT128_CHAR = 'N',
	STDLIB_STRIDED_INT256_CHAR = 'k',
	STDLIB_STRIDED_UINT256_CHAR = 'K',

	STDLIB_STRIDED_FLOAT16_CHAR = 'e',
	STDLIB_STRIDED_FLOAT32_CHAR = 'f',
	STDLIB_STRIDED_FLOAT64_CHAR = 'd',
	STDLIB_STRIDED_FLOAT128_CHAR = 'q'
};

#endif // !STDLIB_STRIDED_DTYPES_H
