'use strict';

// MODULES //

var tape = require( 'tape' );
var replaceAfterLast = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof replaceAfterLast, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function replaces the substring after the last occurrence of a specified search string', function test( t ) {
	var expected;
	var actual;

	actual = replaceAfterLast( 'beep boop', ' ', 'foo' );
	expected = 'beep foo';
	t.strictEqual( actual, expected, 'returns expected value' );

	actual = replaceAfterLast( 'beep boop', 'p', 'foo' );
	expected = 'beep boopfoo';
	t.strictEqual( actual, expected, 'returns expected value' );

	actual = replaceAfterLast( 'Hello, World!', 'o', 'foo' );
	expected = 'Hello, Wofoo';
	t.strictEqual( actual, expected, 'returns expected value' );

	t.end();
});

tape( 'the function replaces the substring after the last occurrence of a specified search string (Unicode characters)', function test( t ) {
	var expected;
	var actual;

	actual = replaceAfterLast( 'beep ğŸ˜€ boop ğŸ˜€ baz', 'ğŸ˜€', 'foo' );
	expected = 'beep ğŸ˜€ boop ğŸ˜€foo';
	t.strictEqual( actual, expected, 'returns expected value' );

	actual = replaceAfterLast( 'ğŸ¤– Robot army ğŸ¤–!', 'ğŸ¤–', 'foo' );
	expected = 'ğŸ¤– Robot army ğŸ¤–foo';
	t.strictEqual( actual, expected, 'returns expected value' );

	actual = replaceAfterLast( 'ğŸº Wolf brothers ğŸº', 'o', 'foo' );
	expected = 'ğŸº Wolf brofoo';
	t.strictEqual( actual, expected, 'returns expected value' );

	t.end();
});

tape( 'the function returns the entire string if the search string is not found', function test( t ) {
	var expected;
	var actual;

	actual = replaceAfterLast( 'beep boop', 'z', 'foo' );
	expected = 'beep boop';
	t.strictEqual( actual, expected, 'returns expected value' );

	actual = replaceAfterLast( 'beep boop', 'baz', 'foo' );
	expected = 'beep boop';
	t.strictEqual( actual, expected, 'returns expected value' );

	t.end();
});

tape( 'the function returns the entire string if the search string is the empty string', function test( t ) {
	var expected;
	var actual;

	actual = replaceAfterLast( 'beep boop', '', 'foo' );
	expected = 'beep boop';
	t.strictEqual( actual, expected, 'returns expected value' );

	t.end();
});
