tape( 'the function replaces the substring after the first occurrence of a specified search string (custom start index)', function test( t ) {
	var replacement;
	var expected;
	var actual;
	var str;

	str = 'beep boop baz';
	replacement = 'foo';
	actual = replaceAfter( str, ' ', 6 );
	expected = 'beep boop foo';
	t.strictEqual( actual, expected, 'returns expected value' );

	str = 'beep boop baz';
	replacement = 'foo';
	actual = replaceAfter( str, replacement, 'p', 6 );
	expected = 'beep boopfoo';

	str = 'beep boop baz';
	replacement = 'foo';
	actual = replaceAfter( str, replacement, 'beep', -2 );
	expected = 'beepfoo';

	str = 'beep boop baz';
	replacement = 'foo';
	actual = replaceAfter( str, replacement, 'beep', 20 );
	expected = '';

	t.end();
});

tape( 'the function returns the empty string if the search string is not found', function test( t ) {
	var replacement;
	var expected;
	var actual;
	var str;

	str = 'beep boop';
	replacement = 'foo';
	actual = replaceAfter( str, replacement, 'z' );
	expected = '';
	t.strictEqual( actual, expected, 'returns expected value' );

	str = 'beep boop';
	replacement = 'foo';
	actual = replaceAfter( str, replacement, 'baz' );
	expected = '';
	t.strictEqual( actual, expected, 'returns expected value' );

	t.end();
});

tape( 'the function returns the replacement string if the search string is the empty string', function test( t ) {
	var replacement;
	var expected;
	var actual;
	var str;

	str = 'beep boop';
	replacement = 'foo';
	actual = replaceAfter( str, replacement, '' );
	expected = 'foo';
	t.strictEqual( actual, expected, 'returns expected value' );

	t.end();
});
