'use strict';

// MODULES //

var tape = require( 'tape' );
var replaceBeforeLast = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof replaceBeforeLast, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function replaces the substring before the last occurrence of a specified search string', function test( t ) {
	var expected;
	var actual;

	actual = replaceBeforeLast( 'beep boop', ' ', 'foo' );
	expected = 'foo boop';
	t.strictEqual( actual, expected, 'returns expected value' );

	actual = replaceBeforeLast( 'beep boop', 'p', 'foo' );
	expected = 'foop';
	t.strictEqual( actual, expected, 'returns expected value' );

	actual = replaceBeforeLast( 'Hello, World!', 'o', 'foo' );
	expected = 'fooorld!';
	t.strictEqual( actual, expected, 'returns expected value' );

	t.end();
});

tape( 'the function replaces the substring before the last occurrence of a specified search string (Unicode characters)', function test( t ) {
	var expected;
	var actual;

	actual = replaceBeforeLast( 'beep üòÄ boop üòÄ baz', 'üòÄ', 'foo' );
	expected = 'fooüòÄ baz';
	t.strictEqual( actual, expected, 'returns expected value' );

	actual = replaceBeforeLast( 'ü§ñ Robot army ü§ñ!', 'ü§ñ', 'foo' );
	expected = 'fooü§ñ!';
	t.strictEqual( actual, expected, 'returns expected value' );

	actual = replaceBeforeLast( 'üê∫ Wolf brothers üê∫', 'o', 'foo' );
	expected = 'fooothers üê∫';
	t.strictEqual( actual, expected, 'returns expected value' );

	t.end();
});

tape( 'the function returns the entire string if the search string is not found', function test( t ) {
	var expected;
	var actual;

	actual = replaceBeforeLast( 'beep boop', 'z', 'foo' );
	expected = 'beep boop';
	t.strictEqual( actual, expected, 'returns expected value' );

	actual = replaceBeforeLast( 'beep boop', 'baz', 'foo' );
	expected = 'beep boop';
	t.strictEqual( actual, expected, 'returns expected value' );

	t.end();
});

tape( 'the function returns the entire string if the search string is the empty string', function test( t ) {
	var expected;
	var actual;

	actual = replaceBeforeLast( 'beep boop', '', 'foo' );
	expected = 'beep boop';
	t.strictEqual( actual, expected, 'returns expected value' );

	t.end();
});
