'use strict';

// MODULES //

var tape = require( 'tape' );
var truncate = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof truncate, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function truncates a string to the specified length', function test( t ) {
	var expected;
	var actual;
	var str;
	var len;

	str = 'beep boop';
	len = 5;
	expected = 'be...';
	actual = truncate( str, len, '...' );
	t.strictEqual( actual, expected, 'returns expected value' );

	str = 'beep boop';
	len = 10;
	expected = 'beep boop';
	actual = truncate( str, len, '...' );
	t.strictEqual( actual, expected, 'returns expected value' );

	str = 'beep boop';
	len = 0;
	expected = '';
	actual = truncate( str, len, '...' );
	t.strictEqual( actual, expected, 'returns expected value' );

	str = 'beep boop';
	len = 1;
	expected = '.';
	actual = truncate( str, len, '...' );
	t.strictEqual( actual, expected, 'returns expected value' );

	str = 'à¤…à¤¨à¥à¤šà¥à¤›à¥‡à¤¦';
	len = 7;
	expected = 'à¤…à¤¨à¥à¤š...';
	actual = truncate( str, len, '...' );
	t.strictEqual( actual, expected, 'returns expected value' );

	str = 'å…­ä¹¦/å…­æ›¸';
	len = 4;
	expected = 'å…­...';
	actual = truncate( str, len, '...' );
	t.strictEqual( actual, expected, 'returns expected value' );

	str = 'ğ’»ğ“Ÿğ’»ğ“Ÿğ’»ğ“Ÿğ’»ğ“Ÿğ’»ğ“Ÿ';
	len = 5;
	expected = 'ğ’»...';
	actual = truncate( str, len, '...' );
	t.strictEqual( actual, expected, 'returns expected value' );

	str = '\uD800ğ“Ÿğ’»ğ“Ÿğ’»ğ“Ÿğ’»ğ“Ÿğ’»ğ“Ÿ';
	len = 4;
	expected = '...';
	actual = truncate( str, len, '...' );
	t.strictEqual( actual, expected, 'returns expected value' );

	t.end();
});

tape( 'the function truncates a string to the specified length (custom ending)', function test( t ) {
	var expected;
	var actual;
	var str;
	var len;

	str = 'beep boop';
	len = 5;
	expected = 'beep|';
	actual = truncate( str, len, '|' );
	t.strictEqual( actual, expected, 'returns expected value' );

	str = 'beep boop';
	len = 10;
	expected = 'beep boop';
	actual = truncate( str, len, '!' );
	t.strictEqual( actual, expected, 'returns expected value' );

	str = 'beep boop';
	len = 3;
	expected = 'be!';
	actual = truncate( str, len, '!' );
	t.strictEqual( actual, expected, 'returns expected value' );

	str = 'à¤…à¤¨à¥à¤šà¥à¤›à¥‡à¤¦';
	len = 7;
	expected = 'à¤…à¤¨à¥à¤šà¥ &';
	actual = truncate( str, len, ' &' );
	t.strictEqual( actual, expected, 'returns expected value' );

	str = 'å…­ä¹¦/å…­æ›¸';
	len = 4;
	expected = 'å…­ä¹¦/&';
	actual = truncate( str, len, '&' );
	t.strictEqual( actual, expected, 'returns expected value' );

	str = 'å…­ä¹¦/å…­';
	len = 4;
	expected = 'å…­ä¹¦/å…­';
	actual = truncate( str, len, 'æ›¸' );
	t.strictEqual( actual, expected, 'returns expected value' );

	str = 'å…­ä¹¦/å…­';
	len = 4;
	expected = 'å…­ä¹¦/å…­';
	actual = truncate( str, len, '' );
	t.strictEqual( actual, expected, 'returns expected value' );

	t.end();
});

tape( 'the function truncates a string to the specified length (truncate ending)', function test( t ) {
	var expected;
	var actual;
	var str;
	var len;

	str = 'beep boop';
	len = 5;
	expected = '|||||';
	actual = truncate( str, len, '||||||' );
	t.strictEqual( actual, expected, 'returns expected value' );

	str = 'beep boop';
	len = 3;
	expected = '~~!';
	actual = truncate( str, len, '~~!' );
	t.strictEqual( actual, expected, 'returns expected value' );

	str = 'aaa ';
	len = 2;
	expected = 'à¤…à¤¨';
	actual = truncate( str, len, 'à¤…à¤¨à¥à¤šà¥à¤›à¥‡à¤¦' );
	t.strictEqual( actual, expected, 'returns expected value' );

	str = 'å…­ä¹¦/å…­';
	len = 3;
	expected = 'å…­ä¹¦æ›¸';
	actual = truncate( str, len, 'æ›¸' );
	t.strictEqual( actual, expected, 'returns expected value' );

	t.end();
});
