'use strict';

var PATH_SEP_POSIX = require( './../lib' );

var parts;
var path;

path = 'foo/bar/baz';
parts = path.split( PATH_SEP_POSIX );
console.log( parts );
// => ['foo','bar','baz']

path = 'foo\\bar\\baz';
parts = path.split( PATH_SEP_POSIX );
console.log( parts );
// => ['foo\\bar\\baz' ]
