'use strict';

/**
* POSIX path segment separator.
*
* @module @stdlib/string/constants/path-sep-posix
* @type {string}
*
* @example
* var PATH_SEP_POSIX = require( '@stdlib/string/constants/path-sep-posix' );
*
* var parts = 'foo/bar/baz'.split( PATH_SEP_POSIX );
* // returns ['foo','bar','baz']
*/

// PATH_SEPARATOR //

/**
* Path segment separator on POSIX platforms.
*
* @constant
* @type {string}
* @default '/'
*/
var PATH_SEP_POSIX = '/';


// EXPORTS //

module.exports = PATH_SEP_POSIX;
