'use strict';

var round = require( '@stdlib/math/base/special/round' );
var randu = require( '@stdlib/random/base/randu' );
var lpad = require( './../lib' );

var str = 'beep';
var n;
var i;

for ( i = 0; i < 100; i++ ) {
	n = round( randu()*10 ) + str.length;
	console.log( lpad( str, n, 'b' ) );
}
