'use strict';

// MAIN //

/**
* Returns whether the string has a surrogate pair at a position.
*
* @param {string} str - input string
* @param {number} pos - position in string
* @returns {boolean} whether the string has a surrogate pair at a position
*
* @example
* var out = isSurrogate( 'last man standing', 4 );
* // returns false
*
* @example
* var out = isSurrogate( 'presidential election', 8 );
* // returns false
*
* @example
* var out = isSurrogate( 'अनुच्छेद', 2 );
* // returns false
*
* @example
* var out = isSurrogate( '🌷', 0 );
* // returns true
*/
function isSurrogate( str, pos ) {
	return str.charCodeAt( pos ) >= 0xd800 && str.charCodeAt( pos ) <= 0xdbff &&
		str.charCodeAt( pos + 1 ) >= 0xdc00 &&
		str.charCodeAt( pos + 1 ) <= 0xdfff;
}


// EXPORTS //

module.exports = isSurrogate;
