'use strict';

// MODULES //

var isString = require( '@stdlib/assert/is-string' ).isPrimitive;
var nextBreak = require( './next_break.js' );


// MAIN //

/**
* Removes the first character of a string.
*
* @param {string} str - input string
* @throws {TypeError} must provide a string primitive
* @returns {integer} Unicode aware length
*
* @example
* var out = len( 'last man standing' );
* // returns 17
*
* @example
* var out = len( 'presidential election' );
* // returns 21
*
* @example
* var out = len( '‡§Ö‡§®‡•Å‡§ö‡•ç‡§õ‡•á‡§¶' );
* // returns 5
*
* @example
* var out = len( 'üå∑' );
* // returns 1
*/
function len( str ) {
	var count;
	var idx;
	var brk;
	if ( !isString( str ) ) {
		throw new TypeError( 'invalid argument. First argument must be a string primitive. Value: `' + str + '`.' );
	}
	count = 0;
	idx = 0;
	brk = nextBreak( str, idx );
	while ( brk < str.length ) {
		idx = brk;
		count += 1;
		brk = nextBreak( str, idx );
	}
	if ( idx < str.length ) {
		count += 1;
	}
	return count;
}


// EXPORTS //

module.exports = len;
