'use strict';

// MODULES //

var codePointAt = require( './code-point-at' );
var isSurrogate = require( './is-surrogate' );
var shouldBreak = require( './should-break' );
var getGraphemeBreakProperty = require( './get-grapheme-break-property.js' );
var getEmojiProperty = require( './get-emoji-property.js' );


// MAIN //

/**
* Returns the next grapheme break in the string after the given index.
*
* @param {string} str - input string
* @param {number} idx - position in string for starting search
* @returns {number} next grapheme break position
*
* @example
* var out = nextBreak( 'last man standing', 4 );
* // returns 5.0
*
* @example
* var out = nextBreak( 'presidential election', 8 );
* // returns 9.0
*
* @example
* var out = nextBreak( '‡§Ö‡§®‡•Å‡§ö‡•ç‡§õ‡•á‡§¶', 1 );
* // returns 3.0
*
* @example
* var out = nextBreak( 'üå∑', 0 );
* // returns 2.0
*/
function nextBreak( str, idx ) {
	var prevEmoji;
	var nextEmoji;
	var midEmoji;
	var prevCP;
	var nextCP;
	var prev;
	var next;
	var mid;
	var i;
	if ( !idx ) {
		idx = 0;
	}
	if ( idx < 0 ) {
		return 0;
	}
	if ( idx >= str.length - 1 ) {
		return str.length;
	}
	prevCP = codePointAt( str, idx );
	prev = getGraphemeBreakProperty( prevCP );
	prevEmoji = getEmojiProperty( prevCP );
	mid = [];
	midEmoji = [];
	for ( i = idx + 1; i < str.length; i++ ) {
		if ( isSurrogate( str, i-1 ) ) {
			continue;
		}
		nextCP = codePointAt( str, i );
		next = getGraphemeBreakProperty( nextCP );
		nextEmoji = getEmojiProperty( nextCP );
		if ( shouldBreak( prev, mid, next, prevEmoji, midEmoji, nextEmoji ) ) {
			return i;
		}
		mid.push( next );
		midEmoji.push( nextEmoji );
	}
	return str.length;
}


// EXPORTS //

module.exports = nextBreak;
