'use strict';

// MODULES //

var tape = require( 'tape' );
var isSurrogate = require( './../lib/is_surrogate.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof isSurrogate, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns whether a provided string contains a surrogate pair', function test( t ) {
	var out;

	out = isSurrogate( '‡§Ö‡§®‡•Å‡§ö‡•ç‡§õ‡•á‡§¶', 2 );
	t.strictEqual( out, false, 'returns expected value' );

	out = isSurrogate( 'üå∑', 0 );
	t.strictEqual( out, true, 'returns expected value' );

	t.end();
});
