'use strict';

// MODULES //

var bench = require( '@stdlib/bench' );
var isNonNegativeInteger = require( '@stdlib/assert/is-nonnegative-integer' ).isPrimitive;
var pkg = require( './../package.json' ).name;
var nextGraphemeClusterBreak = require( './../lib' );


// MAIN //

bench( pkg, function benchmark( b ) {
	var strings;
	var size;
	var out;
	var i;

	strings = [
		'last man standing',
		'presidential election',
		'‡§Ö‡§®‡•Å‡§ö‡•ç‡§õ‡•á‡§¶',
		'üå∑',
		'‰π¶/ÂÖ≠Êõ∏',
		'‡πÄ‚ùÑÔ∏é‡Æ®‡Æø',
		'‡∏Å‡∏¥‡∏¥‡∏Å‡πâ‡πâ‡∏Å‡πá‡πá‡∏Å‡πá‡πá‡∏Å‡∏¥‡∏¥‡∏Å‡πâ‡πâ‡∏Å‡πá‡πá‡∏Å‡∏¥‡∏¥‡∏Å‡πâ‡πâ‡∏Å‡∏¥‡∏¥‡∏Å‡πâ‡πâ‡∏Å‡πá‡πá‡∏Å‡πá‡πá‡∏Å‡∏¥‡∏¥‡∏Å‡πâ‡πâ‡∏Å‡πá‡πá‡∏Å‡∏¥‡∏¥‡∏Å‡πâ‡πâ',
		'Êõ∏ÂÖ≠/‰π¶ÂÖ≠',
		'‹∂∆îŒª É›ñÕ±≈°…ï“Ü ß—∏ÿê“ú“¶…≥Œè',
		'√¢›ùŒù¬Ç“≥”å›æ“Ä∆≥€µ€ßﬁ≥«Å«∏Œì'
	];
	size = strings.length;

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		out = nextGraphemeClusterBreak( strings[ i % size ], 1 );
		if ( !isNonNegativeInteger( out ) ) {
			b.fail( 'should return a nonnegative integer' );
		}
	}
	b.toc();
	if ( !isNonNegativeInteger( out ) ) {
		b.fail( 'should return a nonnegative integer' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});
