'use strict';

// MODULES //

var tape = require( 'tape' );
var nextGraphemeClusterBreak = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof nextGraphemeClusterBreak, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function throws an error if the first argument is not a string primitive', function test( t ) {
	var values;
	var i;

	values = [
		-5,
		3.14,
		-1.0,
		NaN,
		true,
		false,
		null,
		void 0,
		[ 'beep', 'boop' ],
		[ 1, 2, 3 ],
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			nextGraphemeClusterBreak( value, 0 );
		};
	}
});

tape( 'the function throws an error if the second argument is not a nonnegative integer', function test( t ) {
	var values;
	var i;

	values = [
		-5,
		'bar',
		3.14,
		-1.0,
		NaN,
		null,
		void 0,
		[ 'beep', 'boop' ],
		[ 1, 2, 3 ],
		{},
		function noop() {}
	];
	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			nextGraphemeClusterBreak( 'foo', value );
		};
	}
});

tape( 'the function throws an error if a provided position is greater than the string length', function test( t ) {
	var values;
	var i;

	values = [
		[ 'bar', 3 ],
		[ 'string', 7 ]
	];
	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), RangeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			nextGraphemeClusterBreak( value[ 0 ], value[ 1 ] );
		};
	}
});

tape( 'the arity of the function is 2', function test( t ) {
	t.strictEqual( nextGraphemeClusterBreak.length, 2, 'has length 3' );
	t.end();
});

tape( 'the function returns the next grapheme break position in a specified string', function test( t ) {
	var out;

	out = nextGraphemeClusterBreak( 'last man standing', 4 );
	t.strictEqual( out, 5, 'returns expected value' );

	out = nextGraphemeClusterBreak( 'presidential election', 8 );
	t.strictEqual( out, 9, 'returns expected value' );

	out = nextGraphemeClusterBreak( 'अनुच्छेद', 1 );
	t.strictEqual( out, 3, 'returns expected value' );

	out = nextGraphemeClusterBreak( '🌷', 0 );
	t.strictEqual( out, 2, 'returns expected value' );

	t.end();
});

tape( 'the function returns 0 if provided an empty string', function test( t ) {
	var out;

	out = nextGraphemeClusterBreak( '', -2 );
	t.strictEqual( out, 0, 'returns expected value' );

	out = nextGraphemeClusterBreak( '', 2 );
	t.strictEqual( out, 0, 'returns expected value' );

	out = nextGraphemeClusterBreak( '', 0 );
	t.strictEqual( out, 0, 'returns expected value' );

	t.end();
});
