'use strict';

// MODULES //

var codePointAt = require( '@stdlib/string/code-point-at' );
var isSurrogate = require( './is_surrogate.js' );
var shouldBreak = require( './should_break.js' );
var getGraphemeBreakProperty = require( './get_grapheme_break_property.js' );
var getEmojiProperty = require( './get_emoji_property.js' );


// MAIN //

/**
* Returns the next grapheme cluster break (boundary).
*
* @private
* @param {string} str - input string
* @param {NonNegativeInteger} idx - position in string for starting search
* @returns {NonNegativeInteger} next grapheme break position
*
* @example
* var out = nextBreak( 'last man standing', 4 );
* // returns 5
*
* @example
* var out = nextBreak( 'presidential election', 8 );
* // returns 9
*
* @example
* var out = nextBreak( '‡§Ö‡§®‡•Å‡§ö‡•ç‡§õ‡•á‡§¶', 1 );
* // returns 3
*
* @example
* var out = nextBreak( 'üå∑', 0 );
* // returns 2
*/
function nextBreak( str, idx ) {
	var prevEmoji;
	var nextEmoji;
	var midEmoji;
	var prevCP;
	var nextCP;
	var prev;
	var next;
	var len;
	var mid;
	var i;

	len = str.length;
	if ( idx < 0 ) {
		return 0;
	}
	if ( idx >= len - 1 ) {
		return len;
	}
	prevCP = codePointAt( str, idx );
	prev = getGraphemeBreakProperty( prevCP );
	prevEmoji = getEmojiProperty( prevCP );
	mid = [];
	midEmoji = [];
	for ( i = idx + 1; i < len; i++ ) {
		if ( isSurrogate( str, i-1 ) ) {
			continue;
		}
		nextCP = codePointAt( str, i );
		next = getGraphemeBreakProperty( nextCP );
		nextEmoji = getEmojiProperty( nextCP );
		if ( shouldBreak( prev, mid, next, prevEmoji, midEmoji, nextEmoji ) ) {
			return i;
		}
		mid.push( next );
		midEmoji.push( nextEmoji );
	}
	return len;
}


// EXPORTS //

module.exports = nextBreak;
