'use strict';

var removePunctuation = require( './../lib' );

console.log( removePunctuation( 'Double, double, toil and trouble; Fire burn, and cauldron bubble!' ) );
// => 'Double double toil and trouble Fire burn and cauldron bubble'

console.log( removePunctuation( 'This function removes these characters: `{}[]:,!/<>().;~|?\'"' ) );
// => 'This function removes these characters'

console.log( removePunctuation( 'We have to hold the border â€“ at all cost' ) );
// => 'We have to hold the border  at all cost'

console.log( removePunctuation( 'This a sentence without punctuation' ) );
// => 'This a sentence without punctuation'
