'use strict';

// MODULES //

var isInteger = require( '@stdlib/utils/is-integer' ).isPrimitive;
var isString = require( '@stdlib/utils/is-string' ).isPrimitive;


// FUNCTIONS //

/**
* Returns the minimum value.
*
* @private
* @param {number} a - first value
* @param {number} b - second value
* @returns {number} minimum value
*/
function min( a, b ) {
	if ( a < b ) {
		return a;
	}
	return b;
} // end FUNCTION min()

/**
* Returns the maximum value.
*
* @private
* @param {number} a - first value
* @param {number} b - second value
* @returns {number} maximum value
*/
function max( a, b ) {
	if ( a > b ) {
		return a;
	}
	return b;
} // end FUNCTION max()


// STARTS WITH //

/**
* Tests if a string starts with the characters of another string.
*
* @param {string} str - input string
* @param {string} search - search string
* @param {Integer} [position=0] - position at which to start searching
* @throws {TypeError} first argument must be a string primitive
* @throws {TypeError} second argument must be a string primitive
* @throws {TypeError} third argument must be an integer
* @returns {boolean} boolean indicating if the input string starts with the search string
*
* @example
* var out = startsWith( 'Remember the story I used to tell you when you were a boy?', 'Remember' );
* // returns true
* @example
* var out = startsWith( 'Remember the story I used to tell you when you were a boy?', 'Remember, remember' );
* // returns false
* @example
* var out = startsWith( 'To be, or not to be, that is the question.', 'To be' );
* // returns true
* @example
* var out = startsWith( 'To be, or not to be, that is the question.', 'to be' );
* // returns false
*/
function startsWith( str, search, position ) {
	var start;
	var idx;
	var pos;
	if ( !isString( str ) ) {
		throw new TypeError( 'invalid input argument. First argument must be a string primitive. Value: `' + str + '`.' );
	}
	if ( !isString( search ) ) {
		throw new TypeError( 'invalid input argument. Second argument must be a string primitive. Value: `' + search + '`.' );
	}
	if ( arguments.length > 2 ) {
		if ( !isInteger( position ) ) {
			throw new TypeError( 'invalid input argument. Third argument must be an integer. Value: `' + position + '`.' );
		}
		pos = max( position, 0 );
	} else {
		pos = 0;
	}
	start = min( pos, str.length );
	if ( search.length + start > str.length ) {
		return false;
	}
	idx = 0;
	while ( idx < search.length ) {
		if ( str.charCodeAt( start + idx ) !== search.charCodeAt( idx ) ) {
			return false;
		}
		idx += 1;
	}
	return true;
} // end FUNCTION startsWith()


// EXPORTS //

module.exports = startsWith;
