'use strict';

// MODULES //

var isString = require( '@stdlib/assert/is-string' ).isPrimitive;
var isNonNegativeInteger = require( '@stdlib/assert/is-nonnegative-integer' ).isPrimitive;
var hasOwnProp = require( '@stdlib/assert/has-own-property' );
var isPlainObject = require( '@stdlib/assert/is-plain-object' );
var contains = require( '@stdlib/array/base/assert/contains' ).factory;
var truncateGraphemeCluster = require( '@stdlib/string/base/truncate-grapheme-clusters' );
var truncateCodePoint = require( '@stdlib/string/base/truncate-code-points' );
var truncateCodeUnit = require( '@stdlib/string/base/truncate' );
var format = require( '@stdlib/string/format' );


// VARIABLES //

var MODES = [ 'grapheme', 'code_point', 'code_unit' ];
var FCNS = {
	'grapheme': truncateGraphemeCluster,
	'code_point': truncateCodePoint,
	'code_unit': truncateCodeUnit
};
var isMode = contains( MODES );


// MAIN //

/**
* Truncates a string.
*
* @param {string} str - input string
* @param {integer} N - output string length (including ending)
* @param {string} [ending='...'] - custom ending
* @param {Options} [options] - options
* @param {string} [options.mode="grapheme"] - type of "character" to return (must be either `grapheme`, `code_point`, or `code_unit`)
* @throws {TypeError} first argument must be a string
* @throws {TypeError} second argument must be a nonnegative integer
* @throws {TypeError} third argument must be a string
* @throws {TypeError} options argument must be an object
* @throws {TypeError} must provide valid options
* @returns {string} truncated string
*
* @example
* var str = 'beep boop';
* var out = truncate( str, 7 );
* // returns 'beep...'
*
* @example
* var str = 'beep boop';
* var out = truncate( str, 5, '>>>' );
* // returns 'be>>>'
*
* @example
* var str = 'beep boop';
* var out = truncate( str, 10 );
* // returns 'beep boop'
*
* @example
* var str = 'beep boop';
* var out = truncate( str, 0 );
* // returns ''
*
* @example
* var str = 'beep boop';
* var out = truncate( str, 2 );
* // returns '..'
*
* @example
* var str = 'üê∫ Wolf Brothers üê∫';
* var out = truncate( str, 6 );
* // returns 'üê∫ W...'
*/
function truncate( str, N ) {
	var options;
	var ending;
	var nargs;
	var opts;

	if ( !isString( str ) ) {
		throw new TypeError( format( 'invalid argument. First argument must be a string. Value: `%s`.', str ) );
	}
	if ( !isNonNegativeInteger( N ) ) {
		throw new TypeError( format( 'invalid argument. Second argument must be a nonnegative integer. Value: `%s`.', N ) );
	}
	opts = {
		'mode': 'grapheme'
	};
	nargs = arguments.length;

	if (nargs === 2 ) {
		ending = '...';
	} else if ( nargs === 3 ) {
		ending = arguments[ 2 ];
		if ( isPlainObject( ending )) {
			options = ending;
			ending = '...';
		} else if ( !isString( ending ) ) {
			throw new TypeError( format( 'invalid argument. Third argument must be a string. Value: `%s`.', str ) );
		}
	} else {
		ending = arguments[ 2 ];
		if ( !isString( ending ) ) {
			throw new TypeError( format( 'invalid argument. Third argument must be a string. Value: `%s`.', str ) );
		}
		options = arguments[ 3 ];
		if ( !isPlainObject( options ) ) {
			throw new TypeError( format( 'invalid argument. Options argument must be an object. Value: `%s`.', options ) );
		}
	}
	if ( options ) {
		if ( hasOwnProp( options, 'mode' ) ) {
			opts.mode = options.mode;
			if ( !isMode( opts.mode ) ) {
				throw new TypeError( format( 'invalid option. `%s` option must be one of the following: "%s". Value: `%s`.', 'mode', MODES.join( '", "' ), opts.mode ) );
			}
		}
	}
	return FCNS[ opts.mode ]( str, N, ending );
}


// EXPORTS //

module.exports = truncate;
