'use strict';

// MODULES //

var isDurationString = require('@stdlib/assert/is-duration-string');
var parseDuration = require('@stdlib/time/base/parse-duration');
var format = require('@stdlib/string/format');


// MAIN //

/**
* Converts a duration string to milliseconds.
*
* @param {string} str - duration string
* @throws {TypeError} must provide a valid duration string
* @returns {number} milliseconds
*
* @example
* var ms = durationToMilliseconds('1d');
* // returns 86400000
*
* @example
* var ms = durationToMilliseconds('1d2h3m4s5ms');
* // returns 93784005
*/

function durationToMs(str) {
    if (!isDurationString(str)) {
        throw new TypeError(format('Invalid argument. Must provide a valid duration string. Received: `%s`.', str));
    }

    var duration = parseDuration(str);
    return (
        duration.days * 86400000 +
        duration.hours * 3600000 +
        duration.minutes * 60000 +
        duration.seconds * 1000 +
        duration.milliseconds
    );
}

// EXPORTS //

module.exports = durationToMs;
