'use strict';

/**
* Return a high-resolution time.
*
* @module @stdlib/time/tic
*
* @example
* var tic = require( '@stdlib/time/tic' );
*
* var t = tic();
* // returns [<number>,<number>]
*/

// MODULES //

var isBrowser = require( '@stdlib/assert/is-browser' );


// MAIN //

var tic;
if ( isBrowser ) {
	tic = require( './browser.js' );
} else {
	tic = require( './tic.js' );
}


// EXPORTS //

module.exports = tic;
