'use strict';

/**
* Returns the real component of a complex number.
*
* @param {Complex} z - complex number
* @returns {number} real component
*
* @example
* var Complex128 = require( '@stdlib/types/complex/complex128' );
*
* var z = new Complex128( 5.0, 3.0 );
*
* var re = real( z );
* // returns 5.0
*/
function real( z ) {
	return z.re;
} // end FUNCTION real()


// EXPORTS //

module.exports = real;
