'use strict';

var discreteUniform = require( '@stdlib/random/base/discrete-uniform' );
var ind = require( './../lib' );

var modes;
var mode;
var idx;
var out;
var i;

modes = [ 'clamp', 'wrap' ];

for ( i = 0; i < 100; i++ ) {
	idx = discreteUniform( -20, 20 );
	mode = modes[ discreteUniform( 0, modes.length-1 ) ];
	out = ind( idx, 9, mode );
	console.log( '%d => %s(%d,%d) => %d', idx, mode, 0, 9, out );
}
